mapper.select:
select left(report_time, 7) as reportTime,department_id as departmentId,goods_type as goodsType, sum(total_order_quantity) as totalOrderQuantity, sum(total_waybill_quantity) as totalWaybillQuantity, sum(complete_waybill_quantity) as completeWaybillQuantity, sum(anomaly_waybill_quantity) as anomalyWaybillQuantity, sum(refuse_waybill_quantity) as refuseWaybillQuantity, sum(goods_quantity) as goodsQuantity, sum(goods_weight) as goodsWeight from dept_order_daily
1=1 and left(report_time, 7)=#{reportTime}
and dept in
${did}
and goods_type = #{goodsType}
group by reportTime,departmentId,goodsType

select *
from dept_order_daily

    1=1
    and report_time=#{reportTime}

        and department_id in

            ${did}


    and goods_type = #{goodsType}

select report_time as reportTime, goods_type as goodsType, sum(total_order_quantity) as totalOrderQuantity, sum(total_waybill_quantity) as totalWaybillQuantity, sum(complete_waybill_quantity) as completeWaybillQuantity, sum(anomaly_waybill_quantity) as anomalyWaybillQuantity, sum(refuse_waybill_quantity) as refuseWaybillQuantity, sum(goods_quantity) as goodsQuantity, sum(goods_weight) as goodsWeight from( select report_time,goods_type,total_order_quantity,total_waybill_quantity,complete_waybill_quantity,anomaly_waybill_quantity, refuse_waybill_quantity,goods_quantity,goods_weight from dept_order_daily -- where 1=1 and report_time ='20181012' and goods_type=2 and department_id in(1,121)
1=1 and report_time=#{reportTime}
and department_id in
${did}
and goods_type = #{goodsType}
) T_OrderDailyReport group by reportTime,goodsType
