mapper.select:
SELECT date_format(report_time, '%Y-%m') reportTime, department_id departmentId, goods_type goodsType, sum(total_waybill_quantity) totalWaybillQuantity, sum(anomaly_waybill_quantity) anomalyWaybillQuantity, sum(goods_quantity) goodsQuantity, sum(goods_weight) goodsWeight, sum(income_customer_fee) incomeCustomerFee, sum(income_anomaly_fee) incomeAnomalyFee, sum(expend_freight) expendFreight, sum(expend_anomaly_fee) expendAnomalyFee, sum(gross_profit) grossProfit, sum(gross_profit_rate) grossProfitRate FROM dept_waybillfee_daily
where date_format(report_time, '%Y-%m')=#{month}
GROUP BY reportTime, department_id, goods_type

SELECT report_time, department_id, goods_type, total_waybill_quantity,
anomaly_waybill_quantity,goods_quantity, goods_weight, income_customer_fee,
income_anomaly_fee, expend_freight, expend_anomaly_fee, gross_profit, gross_profit_rate
FROM dept_waybillfee_daily

    1=1

        and report_time=#{reportTime}


        and department_id in

            ${did}



        and goods_type = #{goodsType}


order by report_time desc
