mapper.select:
SELECT report_time, department_id, goods_type, total_waybill_quantity,
anomaly_waybill_quantity,goods_quantity, goods_weight, income_customer_fee,
income_anomaly_fee, expend_freight, expend_anomaly_fee, gross_profit, gross_profit_rate
FROM dept_waybillfee_monthly

    1=1

        and report_time=#{reportTime}


        and department_id in

            ${did}



        and goods_type = #{goodsType}


order by report_time desc

SELECT SUM( CASE WHEN goods_type = 1 THEN goods_quantity WHEN goods_type = 2 THEN goods_weight WHEN goods_type = 3 THEN goods_quantity END ) AS x FROM dept_waybillfee_monthly WHERE enabled = 1 and goods_type = #{goodsType}
and department_id in
${did}
and report_time >= DATE_FORMAT(#{startDate}, '%Y-%m' ) and report_time <= DATE_FORMAT(#{endDate}, '%Y-%m' )

SELECT department_id, SUM( CASE WHEN goods_type = 1 THEN goods_quantity WHEN goods_type = 2 THEN goods_weight WHEN goods_type = 3 THEN goods_quantity END ) AS y FROM dept_waybillfee_monthly WHERE enabled = 1 and goods_type = #{goodsType}
and department_id in
${did}
GROUP BY department_id
